/**
 * Contains all scripts generated by carousell
 */
$(document).ready(function() {
	$('.carousel').carousel();
	$('#myCarousel').on('slide', function() {
		$('.blocky').fadeOut(300);
	})
	$('#myCarousel').on('slid', function() {
		$('.blocky').fadeIn(600).animate({
			marginLeft : "+=15%"
		}, 200).animate({
			marginLeft : "-=15%"
		}, 300).animate({
			marginLeft : "+=4%"
		}, 600).animate({
			marginLeft : "-=4%"
		}, 800);
	})
});